const getTransformKey = (options) => {
    const { method, transformDeleteParamsIntoBody } = options;
    if (method === 'GET') {
        return 'params';
    }
    if (method === 'DELETE') {
        return transformDeleteParamsIntoBody ? 'data' : 'params';
    }
    return 'data';
};
export const basicParamsTransform = (params, options) => {
    if (params) {
        const key = getTransformKey(options);
        return Object.assign({ [key]: params }, options);
    }
    return options;
};
